import React, { useEffect, useState } from 'react'

const Timer = () => {
    const [time, setTime] = useState(0)

    useEffect(()=>{
        const id = setInterval(()=>{
            setTime(time=>{
                return time+1
            })
        },1000)
    },[])

  return (
    <div>
        <h1>Timer</h1>
        <h2>{time}</h2>
    </div>
  )
}

export default Timer
